package ejerciciosTA04;

public class Ejercicio1 {

  public static void main(String[] args) {
    // TODO Auto-generated method stub
    int variable1 = 10;
    int variable2 = 20;

    int resultadoSuma = variable1 + variable2; // La variable es el resultado de la operación
    System.out.println("La suma da " + resultadoSuma); // Aquí se imprime por consola dicho resultado

    int resultadoResta = variable1 - variable2;
    System.out.println("La resta da " + resultadoResta);

    int resultadoMultiplicacion = variable1 * variable2;
    System.out.println("La multiplicación da " + resultadoMultiplicacion);

    int resultadoDivision = variable1 / variable2;
    System.out.println("La división da " + resultadoDivision);

    int resultadoModulo = variable1 % variable2;
    System.out.println("El módulo da " + resultadoModulo);

  }

}