package ejerciciosTA04;

public class Ejercicio4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int intN = 20;
		System.out.println("Valor inicial de N = " + intN);
		intN = intN + 77;
		System.out.println("N + 77 = " + intN);
		intN = intN - 3;
		System.out.println("N - 3 = " + intN);
		intN = intN * 2;
		System.out.println("N * 2 = " + intN);
		
	}

}
