package ejerciciosTA04;

public class Ejercicio3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int intX = 5;
		int intY = 9;
		double doubleN = 3.5;
		double doubleM = 6.8;
		System.out.println("intX = " + intX + " intY = " + intY + " doubleN = " + doubleN + " doubleM = " + doubleM);
		System.out.println("X + Y = " + intX + intY);
		System.out.println("X - Y = "  + (intX - intY));
		System.out.println("X * Y = " + intX * intY);
		System.out.println("X / Y = " +  intX / intY);
		System.out.println("X % Y = " + intX % intY);
		System.out.println("N + M = " + doubleN + doubleM);
		System.out.println("N - M = " + (doubleN - doubleM));
		System.out.println("N * M = " + doubleN * doubleM);
		System.out.println("N / M = " + doubleN / doubleM);
		System.out.println("N % M = " + doubleN % doubleM);
		System.out.println("X + N = " + intX + doubleN);
		System.out.println("Y / M = " + intY / doubleM);
		System.out.println("Y % M = " + intY % doubleM);
		System.out.println(intX * 2); // El doble de cada variable
		System.out.println(intY * 2);
		System.out.println(doubleN * 2);
		System.out.println(doubleM * 2);
		System.out.println(intX + intY + doubleN + doubleM); // Suma de todas las variables
		System.out.println(intX * intY * doubleN * doubleM); // Producto de todas las variables
	}

}
