public class Ejercicio9 {

  public static void main(String[] args) {
    // TODO Auto-generated method stub
    for (int i = 0; i < 101; i++) {
      if (i % 2 == 0 && i % 3 == 0) {	// Un if dentro del for para solo printear divisibles entre 2 && 3
        System.out.println(i);
      }

    }
  }

}